import { CartContext } from '../CartContext';
import { ProductsContext } from '../ProductsContext'; // import context

function Cart() {
    const [cartItems] = useContext(CartContext);
    const { removeFromCart } = useContext(ProductsContext); // use context

    const totalCost = cartItems.reduce((total, item) => total + item.product.price * item.quantity, 0);

    return (
        <div>
            <h1>Koszyk</h1>
            <ul>
                {cartItems.map((item, index) => (
                    <li key={index}>
                        {item.product.name} - ${item.product.price} x {item.quantity} = ${item.product.price * item.quantity}
                        <button onClick={() => removeFromCart(item.product)}>Usuń z koszyka</button> {/* add button */}
                    </li>
                ))}
            </ul>
            <h2>Całkowity koszt: ${totalCost}</h2>
        </div>
    );
}

export default Cart;
